            <swiper-slide
              class="photos__slide"
              v-for="(photo, index) in item.photos"
              :key="index"
            >
              <img :src="photo" alt="" style="width: 100%; display: block" />
            </swiper-slide>
            <!-- элемент пагинации -->
          </swiper>
          <div class="swiper-pagination"></div>
        </div>
      </section>
