      <section class="object__map mb-5">
        <div class="location__con">
          <h2 class="title">Локация</h2>
          <div
            class="map-container"
            style="width: 100%; border-radius: 12px; overflow: hidden"
          >
            <yandex-map
              v-if="modelValue == true"
              :key="item.id"
              cursor-grab
              :settings="{
                location: {
                  center: item.coordinates,
                  zoom: 15,
                },
              }"
              width="100%"
              height="55vh"
            >
              <yandex-map-default-scheme-layer />
              <yandex-map-default-features-layer />
              <yandex-map-controls :settings="{ position: 'top left' }">
              </yandex-map-controls>
              <yandex-map-controls
                :settings="{ position: 'bottom left', orientation: 'vertical' }"
              >
              </yandex-map-controls>
              <yandex-map-controls :settings="{ position: 'right' }">
                <yandex-map-zoom-control />
              </yandex-map-controls>
              <YandexMapMarker
                :marker-id="item.id"
                :settings="{
                  position: 'top left-center',
                  coordinates: item.coordinates,
                  properties: { item },
                }"
              >
                <div class="marker__container">
                  <div class="clip">
                    {{ `${item.mapPrice} ${item.currency}` }}
                  </div>
                  <div class="marker" />
                </div>
              </YandexMapMarker>
            </yandex-map>
          </div>
        </div>
      </section>
