  props: {
    item: Object,
    modelValue: Boolean,
  },
  mounted() {
    const images = this.item.photos.filter(function (element, index) {
      return index !== 0 || index !== 1;
    });
    console.log({ images });
    console.log(this.item);
  },
  computed: {
    chunkedPhotos() {
      const chunkSize = 2; // по 2 фото в ряду
      const photos = this.item.photos.slice(); // создаем копию массива
      // исключаем последний элемент
      if (photos.length > 0) {
        photos.pop();
      }
      const chunks = [];
      for (let i = 0; i < photos.length; i += chunkSize) {
        chunks.push(photos.slice(i, i + chunkSize));
      }
      return chunks;
    },
  },
  methods: {
    getSizeClass(rowIndex, photoIndex) {
      // Чередование размеров:
      // В нечетных рядах: первая меньшая, вторая большая
      // В четных рядах: первая большая, вторая меньшая
      if (rowIndex % 2 === 0) {
        return photoIndex === 0 ? "small" : "large";
      } else {
        return photoIndex === 0 ? "large" : "small";
      }
    },
  },
};
